def get_skinbid_discount(skin_name):
    formatted_name = skin_name.replace(" ", "%20").replace("|", "%7C").replace("â˜…", "%E2%98%85")
    url = f"https://skinbid.com/market/csgo/{formatted_name}"
    headers = {"User-Agent": "Mozilla/5.0"}
    response = requests.get(url, headers=headers)
    if response.status_code != 200:
        return None, None, None
    soup = BeautifulSoup(response.text, "html.parser")

    current_price_element = soup.find("span", class_="font-bold text-white text-xl")
    original_price_element = soup.find("span", class_="line-through text-gray-500 text-sm")

    if current_price_element and original_price_element:
        try:
            current_price = float(current_price_element.text.strip().replace("$", "").replace(",", ""))
            original_price = float(original_price_element.text.strip().replace("$", "").replace(",", ""))
            discount = ((original_price - current_price) / original_price) * 100
            return current_price, original_price, discount
        except ValueError:
            return None, None, None
    return None, None, None

async def send_alert(skin, current_price, original_price, discount):
    bot = Bot(token=TELEGRAM_TOKEN, request=HTTPXRequest())
    message = (
        f"ğŸ”¥ *Reducere pe SkinBid!*\n"
        f"*{skin}*\n"
        f"ğŸ’° PreÈ› redus: ${current_price:.2f}\n"
        f"ğŸ’¸ PreÈ› original: ${original_price:.2f}\n"
        f"ğŸ“‰ Discount: {discount:.2f}%"
    )
    await bot.send_message(chat_id=CHAT_ID, text=message, parse_mode="Markdown")

async def send_info_message(text):
    bot = Bot(token=TELEGRAM_TOKEN, request=HTTPXRequest())
    await bot.send_message(chat_id=CHAT_ID, text=text)

async def main():
    await send_info_message("ğŸ” Verific reduceri direct de pe SkinBid...")
    found = False
    for skin in skins:
        current_price, original_price, discount = get_skinbid_discount(skin)
        if discount and discount > 11:
            found = True
            await send_alert(skin, current_price, original_price, discount)
    if not found:
        await send_info_message("âš ï¸ Nicio reducere peste 11% gÄƒsitÄƒ Ã®n acest ciclu.")

